#coding:utf-8import osimport requestsimport hashlibimport jsonserver_url = 'http://103.236.253.247:8087/poker/'appSecret = 'asdfghjkl'def poker_get_clubid(club_name):    url = '/clubInfo'    url = server_url + url    data = appSecret + club_name    md5 = hashlib.md5()    md5.update(data.encode('utf-8'))    sign = md5.hexdigest()    post_data = {'clubNames': club_name, 'sign': sign}    post_data = json.dumps(post_data)    response = requests.post(url, data=post_data)    result = response.json()    if result['msg'] == 'success!':        return result['result']    else:        return Falsedef poker_get_club_player_all(game_club_id):    url = '/playerClubInfo'    url = server_url + url    data = appSecret + game_club_id    md5 = hashlib.md5()    md5.update(data.encode('utf-8'))    sign = md5.hexdigest()    post_data = {'clubIds': game_club_id, 'sign': sign}    post_data = json.dumps(post_data)    response = requests.post(url, data=post_data)    result = response.json()    if result['msg'] == 'success!':        list_game_user_id=[]        list_user = result['result']        for t in list_user:            list_game_user_id.append(t['playerId'])        return list_game_user_id    else:        return Falsedef poker_get_user_name_list(list_user_id):    url = '/playerInfo'    url = server_url + url    str_user_id=''    for t in list_user_id:        if str_user_id == '':            str_user_id = str(t)        else:            str_user_id=str_user_id + ',' + str(t)    data = appSecret + str_user_id    md5 = hashlib.md5()    md5.update(data.encode('utf-8'))    sign = md5.hexdigest()    post_data = {'playerIds': str_user_id, 'sign': sign}    post_data = json.dumps(post_data)    response = requests.post(url, data=post_data)    result = response.json()    if result['msg'] == 'success!':        result = result['result']    else:        return False    return result